    public function testPart1()
    {
        $testCases = [
            ['input' => new DefaultInput('(())'), 'expected' => 0],
            ['input' => new DefaultInput('((('), 'expected' => 3],
            ['input' => new DefaultInput('(()(()('), 'expected' => 3],
            ['input' => new DefaultInput('))((((('), 'expected' => 3],
            ['input' => new DefaultInput('())'), 'expected' => -1],
            ['input' => new DefaultInput('))('), 'expected' => -1],
            ['input' => new DefaultInput(')))'), 'expected' => -3],
            ['input' => new DefaultInput(')())())'), 'expected' => -3],
            ['input' => new DefaultInput($this->actualInput), 'expected' => 232],
        ];

        foreach($testCases as $n => $testCase) {
            $this->assertEquals($testCase['expected'], (new Day01())->part1($testCase['input']), "Testcase #{$n}");
        }
    }

