<?php

namespace App\Tests\AdventOfCode\Year2016;

use PHPUnit\Framework\TestCase;
use App\Input\DefaultInput;
use App\AdventOfCode\Year2016\Day02;

class Day02Test extends TestCase
{
    public function testPart1(): void
    {
        $testCases = [
            ['input' => new DefaultInput('ULL
RRDDD
LURDL
UUUUD'), 'expected' => '1985'],
            ['input' => new DefaultInput($this->actualInput), 'expected' => '24862'],
        ];

        foreach ($testCases as $n => $testCase) {
            $this->assertEquals($testCase['expected'], (new Day02())->part1($testCase['input']), "Testcase #$n");
        }
    }

    public function testPart2(): void
    {
        $testCases = [
            ['input' => new DefaultInput('ULL
RRDDD
LURDL
UUUUD'), 'expected' => '5DB3'],
            ['input' => new DefaultInput($this->actualInput), 'expected' => '46C91'],
        ];

        foreach ($testCases as $n => $testCase) {
            $this->assertEquals($testCase['expected'], (new Day02())->part2($testCase['input']), "Testcase #$n");
        }
    }

    private string $actualInput = '



';
}
